import { Component, OnInit, NgZone } from '@angular/core';
import { Router } from '@angular/router';
import { CrudService } from 'src/app/service/crud.service';
import { FormGroup, FormBuilder } from '@angular/forms';
import { saveAs } from 'file-saver';
import { Medicament } from 'src/app/service/Medicament';


@Component({
  selector: 'app-add-medicament',
  templateUrl: './add-medicament.component.html',
  styleUrls: ['./add-medicament.component.scss']
})
export class AddMedicamentComponent implements OnInit {

  medicamentForm: FormGroup;
  shortLink: string = "";
    loading: boolean = false; 
  file: any;
  imageSrc : any ;

  constructor(
    public formBuilder: FormBuilder,
    private router: Router,
    private ngZone: NgZone,
    public crudService: CrudService,
  ) {
    this.medicamentForm = this.formBuilder.group({
      name: [''],
      description: [''],
      price: [''],
      photo: [''] // initialize with empty string
    });
  }

  ngOnInit(): void {}

  onSubmit() {
    const formData = new FormData();
    formData.append('id',"500")
    formData.append('name', this.medicamentForm.get('name')?.value);

    console.log(this.medicamentForm.get('name')?.value)

    formData.append('description', this.medicamentForm.get('description')?.value);
    console.log(this.medicamentForm.get('description')?.value)

    formData.append('price', this.medicamentForm.get('price')?.value);
    console.log(this.medicamentForm.get('price')?.value)

   // formData.append('photo', this.imageSrc,this.file.imageSrc);
   if (this.file){
    const blob = new Blob([this.file.name], { type: this.file.type });
    formData.append('photo' , this.imageSrc.name.toString())
   }
    console.log(this.imageSrc)

    const medicament: Medicament = {
      id: formData.get('id') as string,
      name: formData.get('name') as string,
      description: formData.get('description') as string,
      price: formData.get('price') as string,
      photo: formData.get('photo') as string
    };
console.log(medicament)
    this.crudService.addMedicament(medicament).subscribe(
      () => {
        console.log('Data added successfully');
        this.ngZone.run(() => this.router.navigateByUrl('/medicament-list'));
      },
      (err) => {
        console.log(err);
        // add proper error handling code
      }
    );
  }

  onFileChanged(event : any) {
    console.log(event);
    this.file = event.target.files[0];

    const reader = new FileReader();
    reader.onload = e => {
        return this.imageSrc = reader.result;
    };

    reader.readAsDataURL(this.file);
}
}
