import { Component, OnInit ,NgZone } from '@angular/core';
import {Router} from '@angular/router';
import { CrudService } from 'src/app/service/crud.service';
import { FormGroup,FormBuilder } from '@angular/forms';

@Component({
  selector: 'app-add-medicament',
  templateUrl: './add-medicament.component.html',
  styleUrls: ['./add-medicament.component.scss']
})
export class AddMedicamentComponent {


  medicamentForm:FormGroup;


  constructor(
    public formBuilder:FormBuilder,
    private router:Router,
    private ngZone:NgZone,
    private crudService : CrudService,


  ){
 this.medicamentForm = this.formBuilder.group({   name:[''],

 description:[''], price:['']})


  }

  ngOnInit(): void{

  }
  onSubmit():any{
    this.crudService.addMedicament(this.medicamentForm.value)
    .subscribe(()=> {
      console.log('Data added successfully')
      this.ngZone.run(()=> this.router.navigateByUrl('/medicament-list'))
    },(err)=>{
      console.log(err)
    })

  }

}
