import { Component } from '@angular/core';
import { CrudService } from 'src/app/service/crud.service';
import { DomSanitizer, SafeUrl } from '@angular/platform-browser';

@Component({
  selector: 'app-medicament-list',
  templateUrl: './medicament-list.component.html',
  styleUrls: ['./medicament-list.component.scss']
})
export class MedicamentListComponent {
  imageSrc!: SafeUrl;
imageLoaded : boolean = false;

  Medicaments:any = [];


  constructor( private crudService: CrudService,private sanitizer: DomSanitizer

  ){

  }
  ngOnInit(): void {

    this.crudService.getMedicaments().subscribe(res => {
      this.imageUrl = this.sanitizer.bypassSecurityTrustUrl(blobUrl);


      this.Medicaments = res ;
      var filename = this.Medicaments[0].photo ;

    console.log(filename)
    const blobUrl = 'blob:http://localhost:4200/7760bd35-68bd-45c3-8c3a-568e2ce87354';

    this.crudService.getImage(filename).subscribe(blobUrl=>{




      this.imageLoaded = true ;
    })

    })



  }

  delete(id:any,i:any){
    console.log(id);
    this.crudService.deleteMedicament(id).subscribe(res => {
      this.Medicaments.splice(i,1); 

    })
  }
}
