onSubmit(){
  const formData = new FormData();
  formData.append("photo",this.files,this.files.name)
  console.log(this.files)
  formData.append('name', this.medicamentForm.value['name'].value);
  formData.append('price', this.medicamentForm.value['price'].value);
  formData.append('description', this.medicamentForm.value['description']?.value);

  console.log(this.medicamentForm.value)
}
// function to handle file input change event

onFileSelected(event : any) {
  this.files = event.target.files[0];
  console.log(this.files[0]);

}





